-- ----------------------------
-- View structure for v_cash_deposit_bounty
-- ----------------------------
DROP VIEW IF EXISTS `v_cash_deposit_bounty`;
CREATE ALGORITHM = UNDEFINED DEFINER = `root`@`localhost` SQL SECURITY DEFINER VIEW `v_cash_deposit_bounty` AS select sum(`tmp`.`total_cash_deposit`) AS `total_cash_deposit`,sum(`tmp`.`total_bounty`) AS `total_bounty`,sum(`tmp`.`month_bounty`) AS `month_bounty`,sum(`tmp`.`yesterday_bounty`) AS `yesterday_bounty`,sum(`tmp`.`today_bounty`) AS `today_bounty` from (select ifnull(round(sum(`ua`.`cash_deposit`),4),0) AS `total_cash_deposit`,0 AS `total_bounty`,0 AS `month_bounty`,0 AS `yesterday_bounty`,0 AS `today_bounty` from `runscore`.`user_account` `ua` where (`ua`.`deleted_flag` = 0) union all select 0 AS `total_cash_deposit`,ifnull(round(sum(`bounty`.`amount`),4),0) AS `total_bounty`,ifnull(round(sum((case when ((`bounty`.`settlement_time` >= str_to_date(date_format(curdate(),'%Y-%m-01 00:00:00'),'%Y-%m-%d %H:%i:%s')) and (`bounty`.`settlement_time` < (str_to_date(date_format(curdate(),'%Y-%m-01 00:00:00'),'%Y-%m-%d %H:%i:%s') + interval 1 month))) then `bounty`.`amount` else 0 end)),4),0) AS `month_bounty`,ifnull(round(sum((case when ((`bounty`.`settlement_time` >= (str_to_date(date_format(now(),'%Y-%m-%d'),'%Y-%m-%d %H:%i:%s') + interval -(1) day)) and (`bounty`.`settlement_time` < str_to_date(date_format(now(),'%Y-%m-%d'),'%Y-%m-%d %H:%i:%s'))) then `bounty`.`amount` else 0 end)),4),0) AS `yesterday_bounty`,ifnull(round(sum((case when ((`bounty`.`settlement_time` >= str_to_date(date_format(now(),'%Y-%m-%d'),'%Y-%m-%d %H:%i:%s')) and (`bounty`.`settlement_time` < (str_to_date(date_format(now(),'%Y-%m-%d'),'%Y-%m-%d %H:%i:%s') + interval 1 day))) then `bounty`.`amount` else 0 end)),4),0) AS `today_bounty` from (select `mo`.`bounty` AS `amount`,`mo`.`confirm_time` AS `settlement_time` from `runscore`.`merchant_order` `mo` where ((`mo`.`confirm_time` is not null) and (`mo`.`order_state` = '4')) union all select `rebate`.`rebate_amount` AS `amount`,`rebate`.`settlement_time` AS `settlement_time` from `runscore`.`order_rebate` `rebate` where (`rebate`.`settlement_time` is not null)) `bounty`) `tmp`;

-- ----------------------------
-- View structure for v_gathering_code_usage
-- ----------------------------
DROP VIEW IF EXISTS `v_gathering_code_usage`;
CREATE ALGORITHM = UNDEFINED DEFINER = `root`@`localhost` SQL SECURITY DEFINER VIEW `v_gathering_code_usage` AS select (case when (`tmp`.`total_order_num` = 0) then 0 else round(((`tmp`.`total_paid_order_num` / `tmp`.`total_order_num`) * 100),1) end) AS `total_success_rate`,(case when (`tmp`.`today_order_num` = 0) then 0 else round(((`tmp`.`today_paid_order_num` / `tmp`.`today_order_num`) * 100),1) end) AS `today_success_rate`,`tmp`.`id` AS `id`,`tmp`.`create_time` AS `create_time`,`tmp`.`gathering_amount` AS `gathering_amount`,`tmp`.`gathering_channel_code` AS `gathering_channel_code`,`tmp`.`payee` AS `payee`,`tmp`.`state` AS `state`,`tmp`.`user_account_id` AS `user_account_id`,`tmp`.`storage_id` AS `storage_id`,`tmp`.`fixed_gathering_amount` AS `fixed_gathering_amount`,`tmp`.`gathering_channel_id` AS `gathering_channel_id`,`tmp`.`in_use` AS `in_use`,`tmp`.`initiate_audit_time` AS `initiate_audit_time`,`tmp`.`audit_type` AS `audit_type`,`tmp`.`total_trade_amount` AS `total_trade_amount`,`tmp`.`total_paid_order_num` AS `total_paid_order_num`,`tmp`.`total_order_num` AS `total_order_num`,`tmp`.`today_trade_amount` AS `today_trade_amount`,`tmp`.`today_paid_order_num` AS `today_paid_order_num`,`tmp`.`today_order_num` AS `today_order_num` from (select `gc`.`id` AS `id`,`gc`.`create_time` AS `create_time`,`gc`.`gathering_amount` AS `gathering_amount`,`gc`.`gathering_channel_code` AS `gathering_channel_code`,`gc`.`payee` AS `payee`,`gc`.`state` AS `state`,`gc`.`user_account_id` AS `user_account_id`,`gc`.`storage_id` AS `storage_id`,`gc`.`fixed_gathering_amount` AS `fixed_gathering_amount`,`gc`.`gathering_channel_id` AS `gathering_channel_id`,`gc`.`in_use` AS `in_use`,`gc`.`initiate_audit_time` AS `initiate_audit_time`,`gc`.`audit_type` AS `audit_type`,ifnull(round(sum((case when (`mo`.`order_state` = '4') then `mo`.`gathering_amount` else 0 end)),4),0) AS `total_trade_amount`,ifnull(sum((case when (`mo`.`order_state` = '4') then 1 else 0 end)),0) AS `total_paid_order_num`,ifnull(sum((case when (`mo`.`order_state` is not null) then 1 else 0 end)),0) AS `total_order_num`,ifnull(round(sum((case when ((`mo`.`submit_time` >= str_to_date(date_format(now(),'%Y-%m-%d'),'%Y-%m-%d %H:%i:%s')) and (`mo`.`submit_time` < (str_to_date(date_format(now(),'%Y-%m-%d'),'%Y-%m-%d %H:%i:%s') + interval 1 day)) and (`mo`.`order_state` = '4')) then `mo`.`gathering_amount` else 0 end)),4),0) AS `today_trade_amount`,ifnull(sum((case when ((`mo`.`submit_time` >= str_to_date(date_format(now(),'%Y-%m-%d'),'%Y-%m-%d %H:%i:%s')) and (`mo`.`submit_time` < (str_to_date(date_format(now(),'%Y-%m-%d'),'%Y-%m-%d %H:%i:%s') + interval 1 day)) and (`mo`.`order_state` = '4')) then 1 else 0 end)),0) AS `today_paid_order_num`,ifnull(sum((case when ((`mo`.`submit_time` >= str_to_date(date_format(now(),'%Y-%m-%d'),'%Y-%m-%d %H:%i:%s')) and (`mo`.`submit_time` < (str_to_date(date_format(now(),'%Y-%m-%d'),'%Y-%m-%d %H:%i:%s') + interval 1 day))) then 1 else 0 end)),0) AS `today_order_num` from ((`runscore`.`gathering_code` `gc` left join `runscore`.`merchant_order` `mo` on((`gc`.`storage_id` = `mo`.`gathering_code_storage_id`))) left join `runscore`.`gathering_channel` `channel` on((`gc`.`gathering_channel_id` = `channel`.`id`))) group by `gc`.`id`,`gc`.`create_time`,`gc`.`gathering_amount`,`gc`.`gathering_channel_code`,`gc`.`payee`,`gc`.`state`,`gc`.`user_account_id`,`gc`.`storage_id`,`gc`.`fixed_gathering_amount`,`gc`.`in_use`,`gc`.`gathering_channel_id`,`gc`.`initiate_audit_time`,`gc`.`audit_type`) `tmp`;

